import { useState, useEffect, ReactNode } from "react";
//import "./App.css";
import View from "./View";
import ToggleButton from "./ToggleButton";
import "./WalletInput.css";

type ChestsSales = {
  total_sol: string | number | boolean | null;
  total_chests: string | number | boolean | null;
};

type Record = {
  amount: string | number | boolean | null;
  reward: string | number | boolean | null;
};

type Price = {
  [key: string]: number;
};

type Cryptoccurency = {
  [key: string]: Price;
};

const getMissionsRewards = (walletList?: Array<string>) => {
  const response = fetch(
    `http://127.0.0.1:5000/api/mission_rewards/CtLWXktxQ8sWwNLaYuJP39j3PHpMeqm3BfPWgJsY18R3`
  );
  return response;
};

const getStakingRewards = (walletList?: Array<string>) => {
  const response = fetch(
    `http://127.0.0.1:5000/api/staking_rewards/CtLWXktxQ8sWwNLaYuJP39j3PHpMeqm3BfPWgJsY18R3`
  );
  return response;
};

const getChestSales = (walletList?: Array<string>) => {
  const response = fetch(
    `http://127.0.0.1:5000/api/chest_sales/CtLWXktxQ8sWwNLaYuJP39j3PHpMeqm3BfPWgJsY18R3`
  );
  return response;
};

function WalletInput() {
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [hasFirstRequestBeenSent, setHasFirstRequestBeenSent] =
    useState<boolean>(false);

  const [missionsResults, setMissionsResults] = useState(0);
  const [stakingResults, setStakingResults] = useState(0);
  const [chestSalesResult, setChestSalesResults] = useState(0);

  const [walletList, setWalletList] = useState<Array<string>>([""]);

  const [prices, setPrices] = useState<Price>({});
  const [view, setView] = useState<boolean>(false);
  const [chestSellerBoolean, setChestSellerBoolean] = useState<boolean>(true);

  //   useEffect(() => {
  //     // fetch data
  //     const dataFetch = async () => {
  //       const foxy_data = await (
  //         await fetch(
  //           "https://api.coingecko.com/api/v3/simple/price?ids=solana%2Cfamous-fox-federation&vs_currencies=usd"
  //         )
  //       )
  //         .json()
  //         .then((data: Cryptoccurency) => {
  //           setFoxyPrice(data["famous-fox-federation"]["usd"]);
  //           setSolanaPrice(data["solana"]["usd"]);
  //         });

  //       //   set state when the data received
  //       //   setFoxyPrice(data);
  //     };

  //     dataFetch();
  //   }, []);

  useEffect(() => {
    // fetch data
    fetch(
      "https://api.coingecko.com/api/v3/simple/price?ids=solana%2Cfamous-fox-federation&vs_currencies=usd"
    )
      .then((response) => response.json())
      .then((data: Cryptoccurency) => {
        setPrices(
          Object.keys(data)
            .map((key) => ({
              [key]: data[key].usd,
            }))
            .reduce((previousValue, currentValue) => ({
              ...previousValue,
              ...currentValue,
            }))
        );
      });

    //   set state when the data received
    //   setFoxyPrice(data);
  }, []);

  const onWalletClick = async () => {
    setIsLoading(true);

    Promise.all([
      getMissionsRewards(),
      getStakingRewards(),
      getChestSales(),
    ]).then(([res1, res2, res3]) => {
      let data = res1.json();
      console.log(data);
      //setMissionsResults(res1.json()[0][0] as number);
    });
  };

  const handleWalletAdd = () => {
    setWalletList([...walletList, ""]);
  };

  const handleWalletRemove = (index: number) => {
    const newWalletList = [...walletList];
    newWalletList.splice(index, 1);
    setWalletList(newWalletList);
  };

  const handleWalletChange = (
    e: React.ChangeEvent<HTMLInputElement>,
    index: number
  ) => {
    const { value } = e.target;
    const list = [...walletList];
    list[index] = value;
    setWalletList(list);
  };

  return (
    <>
      <div className="flex justify-center pt-8 gap-2">
        <div className="flex flex-col">
          <div className="flex flex-col mb-1">
            {walletList.map((wallet, index) => (
              <div key={index} className="flex flex-row">
                <label className="mb-1">
                  <input
                    className="sm:w-72 w-44 h-10 block px-3 py-2 bg-white border border-slate-300 rounded-md text-sm shadow-sm placeholder-slate-400
      focus:outline-none focus:border-sky-600 focus:ring-1 focus:ring-sky-600"
                    placeholder={`Enter your wallet ${index + 1}`}
                    id="wallet"
                    type="text"
                    value={wallet}
                    onChange={(e) => handleWalletChange(e, index)}
                  />
                </label>
                {index === 0 && (
                  <button
                    className="add h-10 ml-1 leading-6 px-2 py-2 font-semibold text-sm shadow rounded-md text-white transition ease-in-out duration-150 flex-1"
                    onClick={handleWalletAdd}
                  >
                    Add wallet
                  </button>
                )}

                {walletList.length > 1 && index !== 0 && (
                  <button
                    className="remove h-10 leading-6 ml-1 px-2 py-2 font-semibold text-sm shadow rounded-md text-white transition ease-in-out duration-150 green-bg flex-1"
                    onClick={() => handleWalletRemove(index)}
                  >
                    Remove wallet
                  </button>
                )}
              </div>
            ))}
          </div>
          <div className="mt-1 h-11 flex rounded-md inner-purple-bg items-center gap-2 justify-around px-4">
            <div className="text-slate-200">
              {" "}
              Have you ever sold your chests?{" "}
            </div>
            <div className="flex items-center justify-center">
              <input
                id="default-checkbox"
                type="checkbox"
                checked={chestSellerBoolean}
                onChange={() => setChestSellerBoolean(!chestSellerBoolean)}
                className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600"
              ></input>
            </div>
          </div>
          <div className="mt-3 mb-6 flex justify-center">
            {isLoading ? (
              <button
                disabled={isLoading}
                onClick={onWalletClick}
                type="button"
                className="h-10 flex justify-center items-center leading-6  px-3 py-2 font-semibold text-sm shadow rounded-md text-white transition ease-in-out duration-150 cursor-not-allowed green-bg"
              >
                <svg
                  className="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Processing...
              </button>
            ) : (
              <button
                onClick={onWalletClick}
                type="button"
                className="h-10 px-3 py-2 font-semibold leading-6 text-sm shadow rounded-md text-white green-bg"
              >
                Submit
              </button>
            )}
          </div>
        </div>
      </div>
      {!isLoading && hasFirstRequestBeenSent && (
        <div>
          <ToggleButton
            textLeft={"Lite"}
            textRight={"Detailed"}
            id={"view"}
            value={view}
            onClick={setView}
          ></ToggleButton>
          <View
            tokenMarket={chestSellerBoolean}
            view={view}
            missionsResults={missionsResults}
            prices={prices}
            stakingResults={stakingResults}
            chestSalesResults={chestSalesResult}
            chestBuysResults={chestBuysResults}
            isLoading={isLoading}
            hasFirstRequestBeenSent={hasFirstRequestBeenSent}
          ></View>
        </div>
      )}
    </>
  );
}

export default WalletInput;
